#!/usr/bin/env python

import os
from os.path import join as pjoin
import parameterized
from .suiteUtils import Paths

templateRoot = pjoin(os.path.dirname(parameterized.__file__),'htmlTemplates')

CSS = open(pjoin(templateRoot,'template.css')).read()

basicHtml= open(pjoin(templateRoot,'basic.html')).read().format(CSS)

basicHtmlJS= open(pjoin(templateRoot,'basicJS.html')).read().format(CSS)

caseHtml= open(pjoin(templateRoot,'case.html')).read().format(CSS)

def fileCopy(sourceFile, targetDir):
    src = open(pjoin(templateRoot,sourceFile)).read()
    with open(os.path.join(targetDir,sourceFile),'w') as fp:
        fp.write(src)

if __name__=='__main__':
    print(caseHtml)
